(8, 1, 1, 0, NULL, NULL, NULL, '/*\r\n * Problem: Cirno''s Perfect Math Class @ Magimagi\r\n * Solution: Ans = (N + 1) - PI(DIGITS) - (''10'', ''11'', ''20'', ''21'') * PI(DIGITS OF OTHERS)\r\n * Author: Magimagi\r\n * Date: 2014-5-12\r\n */\r\n#include <cstdio>\r\n#include <cstring>\r\n#include <cstdlib>\r\nchar Input[50005];\r\nint Trans[50005], Cnt[50];\r\ninline int max(int a, int b)\r\n{\r\n	return a > b ? a : b;\r\n}\r\nclass BigNum\r\n{\r\npublic:\r\n	int Num[50005];\r\n	int Length;\r\n	int Remainder;\r\n	int& operator [](int x) { return Num[x]; }\r\n	BigNum()\r\n	{\r\n		memset(Num, 0, sizeof(Num));\r\n		Length = 0;\r\n	}\r\n	BigNum(char* Input)\r\n	{\r\n		memset(Num, 0, sizeof(Num));\r\n		Length = strlen(Input);\r\n		for (int i = 1; i <= Length; ++i)\r\n			Num[i] = Input[Length - i] - ''0'';\r\n	}\r\n	BigNum& operator *=(int x)\r\n	{\r\n		for (int i = 1; i <= Length; ++i)\r\n			Num[i] *= x;\r\n		for (int i = 1; i <= Length; ++i)\r\n			if (Num[i] > 9) {\r\n				if (i == Length)\r\n					++Length;\r\n				Num[i + 1] += Num[i] / 10;\r\n				Num[i] %= 10;\r\n			}\r\n		return *this;\r\n	}\r\n	BigNum& operator /=(int x)\r\n	{\r\n		Remainder = 0;\r\n		for (int i = Length; i > 0; --i) {\r\n			Num[i] += Remainder * 10;\r\n			Remainder = Num[i] % x;\r\n			Num[i] /= x;\r\n			if (i == Length && Num[i] == 0)\r\n				--Length;\r\n		}\r\n		return *this;\r\n	}\r\n	BigNum& Inc()\r\n	{\r\n		Length = max(1, Length);\r\n		++Num[1];\r\n		for (int i = 1; i <= Length; ++i) {\r\n			if (Num[i] > 9) {\r\n				Num[i] -= 10;\r\n				++Num[i + 1];\r\n				if (i == Length)\r\n					++Length;\r\n			}\r\n		}\r\n		return *this;\r\n	}\r\n	void Println()\r\n	{\r\n		if (Length == 0)\r\n			printf("0");\r\n		else\r\n			for (int i = Length; i > 0; --i)\r\n				printf("%d",Num[i]);\r\n		puts("");\r\n	}\r\n};\r\nBigNum operator *(BigNum A, int B)\r\n{\r\n	BigNum Res;\r\n	if (B == 0)\r\n		return Res;\r\n	Res.Length = A.Length;\r\n	for (int i = 1; i <= Res.Length; ++i)\r\n		Res[i] = A[i] * B;\r\n	for (int i = 1; i <= Res.Length; ++i)\r\n		if (Res[i] > 9) {\r\n			if (i == Res.Length)\r\n				++Res.Length;\r\n			Res[i + 1] += Res[i] / 10;\r\n			Res[i] %= 10;\r\n		}\r\n	return Res;\r\n}\r\nBigNum operator *(BigNum A, BigNum B)\r\n{\r\n	BigNum Res;\r\n	Res.Length = A.Length + B.Length - 1;\r\n	for (int i = 1; i <= A.Length; ++i)\r\n		for (int j = 1; j <= B.Length; ++j)\r\n			Res[i + j - 1] += A[i] * B[j];\r\n	for (int i = 1; i <= Res.Length; ++i)\r\n		if (Res[i] > 9) {\r\n			Res[i + 1] += Res[i] / 10;\r\n			Res[i] %= 10;\r\n			if (i == Res.Length)\r\n				++Res.Length;\r\n		}\r\n	return Res;\r\n}\r\nBigNum operator -(BigNum A, BigNum B)\r\n{\r\n	BigNum Res;\r\n	Res.Length = max(A.Length, B.Length);\r\n	for (int i = 1; i <= Res.Length; ++i) {\r\n		Res[i] += A[i] - B[i];\r\n		if (Res[i] < 0) {\r\n			Res[i] += 10;\r\n			--Res[i + 1];\r\n		}\r\n	}\r\n	while (Res.Length > 0 && Res[Res.Length] == 0)\r\n		--Res.Length;\r\n	return Res;\r\n}\r\nBigNum Power(int Base, int P)\r\n{\r\n	char one[] = "1";\r\n	BigNum Now(one);\r\n	if (P <= 0)\r\n		return Now;\r\n	for (int i = 1; i <= P; ++i)\r\n		Now *= Base;\r\n	return Now;\r\n}\r\nBigNum Eval()\r\n{\r\n	memset(Cnt, 0, sizeof(Cnt));\r\n	memset(Trans, 0, sizeof(Trans));\r\n	BigNum N(Input);\r\n	BigNum T = N;\r\n	if (N.Length == 1 && N[1] == 0)\r\n		return N;\r\n	int Tail = 0;\r\n	while (T.Length != 0) {\r\n		T /= 3;\r\n		++Cnt[Trans[++Tail] = T.Remainder];\r\n	}\r\n	int A = 0, // 10\r\n		B = 0, // 11\r\n		C = 0, // 20\r\n		D = 0; // 21\r\n	for (int i = Tail; i > 1; --i)\r\n		if (Trans[i] == 1 && Trans[i - 1] == 0)\r\n			A += 2;\r\n		else if (Trans[i] == 1 && Trans[i - 1] == 1)\r\n			B += 1;\r\n		else if (Trans[i] == 2 && Trans[i - 1] == 0)\r\n			C += 4;\r\n		else if (Trans[i] == 2 && Trans[i - 1] == 1)\r\n			D += 2;\r\n	BigNum DDPower2, DPower2, Power2, DPower3, Power3;\r\n	DDPower2 = Power(2, Cnt[1] - 2);\r\n	if (Cnt[1] <= 1)\r\n		DPower2 = DDPower2;\r\n	else\r\n		DPower2 = DDPower2 * 2;\r\n	if (Cnt[1] <= 0)\r\n		Power2 = DPower2;\r\n	else\r\n		Power2 = DPower2 * 2;\r\n	DPower3 = Power(3, Cnt[2] - 1);\r\n	if (Cnt[2] <= 0)\r\n		Power3 = DPower3;\r\n	else\r\n		Power3 = DPower3 * 3;\r\n	return N.Inc() - Power2 * Power3\r\n				   - DPower2 * Power3 * A\r\n				   - DDPower2 * Power3 * B\r\n				   - Power2 * DPower3 * C\r\n				   - DPower2 * DPower3 * D;\r\n}\r\nint main()\r\n{\r\n	int T, N;\r\n	for (scanf("%d\\n", &T); T; --T) {\r\n		scanf("%s", Input);\r\n		Eval().Println();\r\n	}\r\n	return 0;\r\n}', 'Cpp11', '2014-07-17 08:50:38', 1024, 0),
